import React, { useState, useEffect } from 'react'

import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import Icons from "@material-ui/icons";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import Calibration from "./Calibration";
import Grid from '@material-ui/core/Grid';
import PickSingleColor from './PickSingleColor';
import { nbaLogoMap } from '../util/logos';
import Arrow from '../assets/arrow.svg';

const useStyles = makeStyles((theme) => ({

    '@keyframes livePulse': {
        from: {
            background:'#ff0000',
        },
        to: {
            background:'#990000',
        },
    },
    "@keyframes shadowPulse": {
        "0%": {
            boxShadow:'0px 0px 20px rgba(255,255,255,.5), inset 0px 0px 20px rgba(0,0,0,.2)'
        },
        "50%": {
            boxShadow:'0px 0px 20px rgba(255,255,255,.2), inset 0px 0px 20px rgba(0,0,0,.5)'
        },
        "100%": {
            boxShadow:'0px 0px 20px rgba(255,255,255,.5), inset 0px 0px 20px rgba(0,0,0,.2)'
        }
    },
    wrapper: {
        overflow:'hidden!important',
        // border: '1px solid black',
        width: '99vw',
        height: '101vh',
        display: 'block',
        padding:'40px 1vw',
        margin: 'auto',
        marginTop: -22,
        justifyContent: 'center',
        position: 'relative',
        fontFamily: 'Roboto Mono',
        background: (props) => props.lightColorHex,
    },
    modalBox: {
        position: 'fixed',
        top: '50%',
        left: '50%',
        width:'400px',
        padding:'50px',
        borderRadius: 20,
        transform: 'translate(-50%, -50%)',
        textAlign: 'center',
        // boxShadow:'0 0 100px rgba(255,255,255,1)',
        backgroundColor: "white",
        animation: '$lightGlow 20s ease infinite',
        '@media (max-width:600px)': {
            width: '100vw',
            height:'100vh',
            paddingTop:200
        },
    },
    live: {
        fontFamily: 'Roboto Mono',
        fontWeight: 'bold',
        fontSize: '1em',
        display:'inline-flex',
        borderRadius:50,
        padding: '10px 15px',
        color: 'white',
        marginBottom:10,
        // border:'2px solid white',
        animation: '$livePulse 1s infinite alternate linear',
        boxShadow:'0px 0px 20px rgba(255,255,255,1)',
        [theme.breakpoints.down('xs')]: {
            color:'transparent',
            width:20,
            height:20,
            padding:0
        }
    },
    logoItems: {
        justifyContent:'center',
        textAlign:'center',
        position:'relative'
    },
    logoContainer: {
        position:'relative',
        overflow:'hidden',
        width: 300,
        height: 300,
        maxHeight: '100%',
        margin:'0 auto',
        borderRadius: '100%',
        animation: '$shadowPulse 5s linear infinite',
        backgroundColor:'rgba(255,255,255,.1)',
        [theme.breakpoints.down('md')]: {
            width: 150,
            height: 150,
        },
        [theme.breakpoints.down('sm')]: {
            width: 100,
            height: 100,
        },
        [theme.breakpoints.down('xs')]: {
            border:'2px solid white',
            width: 20,
            height: 20,
        }
    },
    logo: {
        width:600,
        height:'auto',
        marginLeft:'-50%',
        marginTop:'-50%',
        [theme.breakpoints.down('md')]: {
            width: 300,
            height: 300,
            marginLeft:'-55%',
            marginTop:'-55%',
        },
        [theme.breakpoints.down('sm')]: {
            width: 200,
            height: 200,
            marginLeft:'-50%',
            marginTop:'-50%',
        },
        [theme.breakpoints.down('xs')]: {
            display:'none'
        }
    },
    gameInfo: {
        margin:'auto',
        marginTop:'15vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',        /* for single line flex container */
        alignContent: 'center'      /* for multi-line flex container */
    },
    nameScore: {
        color:'white',
        display:'block',
        position:'relative'
    },
    text: {
        color:'white',
        fontWeight:'bold',
        lineHeight:'1em',
        margin:0,
        textShadow:'0px 0px 10px rgba(0,0,0,.5)',
        textTransform: 'uppercase',
    },
    teamName: {
        fontSize: '3vw',
        textAlign:'center',
        [theme.breakpoints.down('sm')]: {
            fontSize:'3vw'
        },
        position:'absolute',
        display:'block',
        width:'100%',
        top:'-3vw'
    },
    teamScore: {
        fontSize: '9vw',
        margin: '10px 0',
        textAlign:'center',
        [theme.breakpoints.down('xs')]: {
            fontSize: '15vw',
        }
    },
    timeContainer: {
        background:'white',
        width:'auto',
        paddingTop:15,
        display:'inline-block',
        borderRadius:'29px',
        boxShadow:'0px 0px 10px rgba(0,0,0,.5)',
        overflow:'hidden'
    },
    small: {
        fontSize:'.2em',
